<?php	$devices = $Sonoff->getDevices();?><div class='center'>	<?php		if ( isset( $devices ) && !empty( $devices ) ):			foreach ( $devices as $device_group ): ?>				<?php foreach ( $device_group->names as $key => $devicename ): ?>					<?php					$img = _RESOURCESDIR_."img/device_icons/".$device_group->img."_off.png";					?>					<div class='box_device'					     data-device_id='<?php echo $device_group->id; ?>'					     data-device_group='<?php echo count( $device_group->names ) > 1 ? "multi" : "single"; ?>'					     data-device_ip='<?php echo $device_group->ip; ?>'					     data-device_relais='<?php echo $key + 1; ?>'					>						<div class="animated rubberBand">							<img data-icon='<?php echo $device_group->img; ?>' src='<?php echo $img; ?>' alt=''>						</div>												<div class='box_device_name'>							<?php echo $devicename; ?>						</div>					</div>				<?php				endforeach;			endforeach;		else: ?>			<br/>			<br/>			<p>				<?php echo __( "NO_DEVICES_FOUND", "STARTPAGE" ); ?>			</p>		<?php endif; ?></div><script type='text/javascript' src='<?php echo _RESOURCESDIR_; ?>js/start.js'></script>